const readlineSync = require("readline-sync");


let idTask = 0;

function addTask(taskList){
    console.log("\n==============================================");
    console.log("                  ADD TASK                    ");
    console.log("==============================================");

    let newName = readlineSync.question("Write the Task name: ");
    let newDescription = readlineSync.question("Write the Task description: ");
    idTask++;

    const newTask = {
        
          id: idTask,
          taskName: newName,
          description: newDescription,
          completed: false,
        };
      
   taskList= taskList.push(newTask);
       
    
    return(
        new Promise((resolve, reject)=>{
       setTimeout(() => {
          resolve(taskList)  
        }, 1000);
            
        }
    ))
}

module.exports = addTask;