const readlineSync = require("readline-sync");

function deleteTask(taskList){
    console.log("\n==============================================");
    console.log("                 DELETE TASK                    ");
    console.log("==============================================");

    let itemDelete = parseInt(readlineSync.question("Write the ID to delete: "));

    let indice = taskList.findIndex((item) => item.id === itemDelete);
 
    if (indice != -1) {
        taskList.splice(indice, 1);
        console.log(`\nTask with id: ${itemDelete} was deleted:`);        
      } else {
        console.log(`\nThe task with id: ${itemDelete} doesn't exist.`);
      }
    
      return(
        new Promise((resolve, reject) => {
            setTimeout(()=>
                resolve(taskList), 1000)            
        }))
}

module.exports = deleteTask