function printTaskList(taskList){
    console.log("\n==============================================");
    console.log("                   TASK LIST                  ");
    console.log("==============================================");


   for (task in taskList) {
    console.log(`
    Id: ${taskList[task].id}
    Task Name: ${taskList[task].taskName}
    Description: ${taskList[task].description}
    Completed: ${taskList[task].completed}    
    `)   
    }
   }

module.exports = printTaskList